function onMPHSlide(e){document.getElementById("MPHLabel").innerHTML=e}function onKPHSlide(e){document.getElementById("KPHLabel").innerHTML=e}function onRPMSlide(e){document.getElementById("RPMLabel").innerHTML=e}function calculateTireDiameter(){var e=2*(parseInt(document.getElementById("tireWidth").value)/25.4*(parseInt(document.getElementById("aspectRatio").value)/100))+parseInt(document.getElementById("wheelDiameter").value);document.getElementById("tireDiameter").value=e.toFixed(2)}function selectedGearRatio(){for(var e=document.getElementsByName("selectedGear"),t=0;t<e.length;t++)if(e[t].checked){var n=parseInt(t)+1;return 1==n?document.getElementById("firstgear").value:2==n?document.getElementById("secondgear").value:3==n?document.getElementById("thirdgear").value:4==n?document.getElementById("fourthgear").value:5==n?document.getElementById("fifthgear").value:6==n?document.getElementById("sixthgear").value:e[t].value}}function MPHChangeCalculation(){var e=document.getElementById("MPHSlider").value;onMPHSlide(e);var t=document.getElementById("finaldrive").value,n=document.getElementById("tireDiameter").value,l=e*t*selectedGearRatio()*336.13/n;onRPMSlide((document.getElementById("RPMSlider").value=l).toFixed(0))}function RPMChangeCalculation(){var e=document.getElementById("RPMSlider").value;onRPMSlide(e);var t=document.getElementById("finaldrive").value,n=e*document.getElementById("tireDiameter").value/(selectedGearRatio()*t*336.13);onMPHSlide((document.getElementById("MPHSlider").value=n).toFixed(0))}