function onMPHSlide(e){document.getElementById("MPHLabel").innerHTML=e}function onKPHSlide(e){document.getElementById("KPHLabel").innerHTML=e}function onRPMSlide(e){document.getElementById("RPMLabel").innerHTML=e}function calculateTireDiameter(){var e=parseInt(document.getElementById("tireWidth").value),t=2*(parseInt(document.getElementById("aspectRatio").value)/100*(e/25.4))+parseInt(document.getElementById("wheelDiameter").value);document.getElementById("tireDiameter").value=t.toFixed(2)}function selectedGearRatio(){var e=document.getElementById("firstgear").value;document.getElementById("secondgear").value,document.getElementById("thirdgear").value,document.getElementById("fourthgear").value,document.getElementById("fifthgear").value,document.getElementById("sixthgear").value;return e}function MPHChangeCalculation(){var e=document.getElementById("MPHSlider").value;onMPHSlide(e);var t=document.getElementById("finaldrive").value,n=document.getElementById("tireDiameter").value,l=e*t*selectedGearRatio()*336.13/n;onRPMSlide((document.getElementById("RPMSlider").value=l).toFixed(0))}function RPMChangeCalculation(){var e=document.getElementById("RPMSlider").value;onRPMSlide(e);var t=document.getElementById("finaldrive").value,n=e*document.getElementById("tireDiameter").value/(selectedGearRatio()*t*336.13);onMPHSlide((document.getElementById("MPHSlider").value=n).toFixed(0))}